INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'штрифель',N'яблони',N'плодово-ягодные-деревья-и-кустарники',N'Штрифель',400,N'Штрейфлинг — старинный осенний сорт яблони родом из Прибалтики, полученный через естественное (природное) опыление. Другие названия сорта — Штрифель, Осеннее полосатое, Старостино, Лифляндское Графенштейнское, Обрезковое.')
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'антоновка',N'яблони',N'плодово-ягодные-деревья-и-кустарники',N'Антоновка',400,N'Антоновка – главный сорт яблонь в России. Неприхотливое и высокоплодородное дерево существует в зоне рискованного земледелия давно. В ее историю вложили силы не столько квалифицированные селекционеры, сколько неустанные и неугомонные любители — садоводы. ');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'мраморная',N'груши',N'плодово-ягодные-деревья-и-кустарники',N'Мраморная',500,N'Вкусная груша, что можно скушать.');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'дюймовочка',N'груши',N'плодово-ягодные-деревья-и-кустарники',N'Дюймовочка',400,N'Осенний сорт селекции ВСТИСП, полученный от опыления межвидового гибрида №9 смесью пыльцы южных сортов. Авторы: Ю. А. Петров, Н. В. Ефимова. Распространен в приусадебных садах Московской и соседних областей. Районирован в Центральном регионе.');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'баклажан-принц',N'овощи',N'семена-растений',N'Баклажан Принц',20,N'Раннеспелый (95-104 дня) сорт для открытого грунта и пленочных укрытий. Растение компактное, высотой 60-75 см. Плоды цилиндрические, черно-фиолетовые, длиной 20-30 см, массой 150-200 г. Мякоть белая, нежная, вкусная, без горечи. Урожайность 5,7-6 кг/кв.м.')
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'горох-русский-размер',N'овощи',N'семена-растений',N'Горох Русский Размер',30, N'Горох Русский Размер - любимое лакомство детворы и всех взрослых! Наш любимый сорт порадует  вас своим невиданным размером плодов и исключительным вкусом. Крупные, не менее 1 см в диаметре, горошины так и хочется быстрее сорвать с грядки и съесть! Вкус нежный и сладкий, они хороши в свежем виде, а также пригодны для замораживания и консервирования. Сорт позднеспелый, урожайный и неприхотливый. Обязателен для каждого огорода!');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'астра-розовый-ежик',N'цветы',N'семена-растений',N'Астра Розовый ежик',20, N'Астра Розовый Ежик – новый сорт, по степени махровости и плотности соцветий не уступающий, а даже превосходящий прочие известные сорта астр! Диаметр цветков 13 см, на каждом кусте формируется до 12 соцветий. Цвет – нежно-розовый, с жёлтой серединкой и белыми краешками лепестков. Идеальные свойства формы: астра не деформируется, если её положить на плоскую поверхность, а также долго стоит в букете (до 2-х недель в вазах). ');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'венидиум-принц-зулу',N'цветы',N'семена-растений',N'Венидиум Принц зулу',20,N'Сорт с крупными, до 10 см в диаметре соцветиями на длинных цветоносах. Растение кустистое, раскидистое. Отличная срезка - цветки долго сохраняют свежесть в букете.');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'базилик-арарат',N'пряные-травы',N'семена-растений',N'Базилик Арарат',15,N'Среднеспелый сорт. Период от полных всходов до начала цветения 71 день. Растение прямостоячее, полураскидистое, высотой до 60 см. Масса одного растения 25-35 г.');
INSERT INTO [Product] ([ProductId],[SubcategoryId],[CategoryId],[Alias],[Price],[Descriprion]) VALUES (N'укроп-аллигатор',N'пряные-травы',N'семена-растений',N'Укроп Аллигатор',15,N'Среднепоздний сорт кустовой формы. Период от всходов до уборки на зелень около 45 дней, на специи - до 115 дней. Долго не выпускает зонтик, возможен многократный сбор зелени. ');
